mapper.select:
SELECT id, code_group_id, code_id, code_name, sort_order, use_yn, description, created_by, created_at, updated_by, updated_at 
FROM common_code
WHERE id = #{id}

mapper.insert:
INSERT INTO common_code (
    code_group_id, code_id, code_name, sort_order, use_yn, description, created_by, created_at, updated_by, updated_at
) VALUES (
    #{codeGroupId}, #{codeId}, #{codeName}, #{sortOrder}, #{useYn}, #{description}, 'SYSTEM', CURRENT_TIMESTAMP, 'SYSTEM', CURRENT_TIMESTAMP
)
